INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Charming Danang Show Ticket with Hotel Pickup', N'Khám phá ẩm thực đường phố Đà Nẵng bằng tour xe máy. Thưởng thức ẩm thực Việt Nam đích thực, tham quan các địa danh như Nhà thờ Đà Nẵng. Đặt ngay bây giờ để có một cuộc phiêu lưu thú vị!
