INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Hands Body Massage 120 mins with Aroma Oils', N'Chúng tôi cung cấp dịch vụ đón/trả miễn phí cho du khách lưu trú tại trung tâm thành phố Đà Nẵng
Thời gian đón được xác nhận trước 1 ngày bởi nhà điều hành tour của chúng tôi
Dịch vụ khách hàng trực tuyến 24/7', N'
~Học cách nấu những món ăn đơn giản nhưng ngon miệng từ khắp Việt Nam, như salad rau theo mùa từ Sài Gòn, phở cuốn từ Hà Nội, bánh xèo Việt Nam hoặc rau muống chiên từ Hội An cùng một gia đình địa phương
~~
~Nhận được những hiểu biết sâu sắc và những câu chuyện về cuộc sống, gia đình, văn hóa địa phương từ người chủ nhà, những người có thể giao tiếp với bạn bằng tiếng Anh và tiếng Quan Thoại
~~
~Lớp học nấu ăn kéo dài khoảng 2 tiếng rưỡi. Sau khi dùng bữa trưa hoặc bữa tối ngon miệng, hãy đến chợ đêm gần đó, Công viên Châu Á hoặc Lotte Mart. Hoặc gọi taxi để đưa bạn từ nhà của gia đình địa phương đến thẳng chỗ ở của bạn',N'139 Nguyễn Văn Thoại, An Hải Đông, Sơn Trà, Đà Nẵng 550000, Việt Nam', N'139 Nguyễn Văn Thoại, An Hải Đông, Sơn Trà, Đà Nẵng 550000, Việt Nam', '/tours/127');
INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Hot Mineral Mud Bath in Da Nang', N'Có cơ hội tìm hiểu về ẩm thực và văn hóa Việt Nam khi bạn tham gia lớp học nấu ăn thú vị này trong chuyến đi đến Đà Nẵng!
Nhận hướng dẫn từng bước về cách nấu các món ăn Việt Nam đơn giản nhưng ngon miệng từ gia đình địa phương thân thiện của bạn
Áp dụng phương pháp thực hành vào bài học của bạn, sau đó nếm thử những sáng tạo của riêng bạn trong bữa trưa hoặc bữa tối
Hiểu rõ hơn về cuộc sống, văn hóa và câu chuyện địa phương từ chủ nhà của bạn, những người cũng thông thạo tiếng Anh và tiếng Trung', N'
~ Đón tại điểm hẹn. Điểm đến đầu tiên là bán đảo Sơn Trà, tham quan chùa Linh Ứng với tượng Quan Âm cao nhất Việt Nam với chiều cao 67 mét.
14:15
~~
~Xe của chúng tôi đưa bạn dọc theo con đường biển để tận hưởng vẻ đẹp của nắng vàng, cát trắng trải dài trên bãi biển Mỹ Khê. Đến điểm đến tiếp theo - Ngũ Hành Sơn (Ngũ Hành Sơn), nơi có hệ thống hang động huyền bí và làng nghề đá cẩm thạch Non Nước.
16:00
~~
~ Di chuyển đến Phố cổ Hội An, tham quan Cầu Nhật Bản, Bảo tàng văn hóa Sa Huỳnh, những ngôi nhà cổ, v.v., dạo quanh phố đèn lồng và mua sắm tại chợ đêm Nguyễn Hoàng.
17:30
~~
~Ăn tối tại nhà hàng Hội An với các món đặc sản địa phương.
19:00
~~
~Nhóm tập trung và lên xe quay về điểm đón.
20:00
~~
~Trả khách tại điểm hẹn. Chuyến tham quan kết thúc.
20:30',N'Đà Nẵng, Việt Nam', N'Đà Nẵng, Việt Nam', '/tours/128');
