INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Hidden Ha Long with a photographer', N'Trải nghiệm này tôi sẽ đưa bạn về nhà và bạn có thể tham gia lớp học ẩm thực về các món ăn truyền thống địa phương của người dân tộc Hmong ở Sapa.
Bạn sẽ được tự tay làm những món ăn và thưởng thức cùng người dân địa phương, lắng nghe những câu chuyện, làn điệu dân ca của họ. Và chúng ta sẽ cùng nhau nâng ly rượu vang địa phương.', N'
~Nhà trekking Sài Gòn
Thời gian: 3 tiếng (xấp xỉ)',N'30 Bãi Cháy, Thành phố Hạ Long, Quảng Ninh, Việt Nam', N'30 Bãi Cháy, Thành phố Hạ Long, Quảng Ninh, Việt Nam', '/tours/216');
INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'[Hikking & Trekking] Tour to Mai Ha, Kha, Te, Buoc, Mai Hich Village (Full day)', N'Chương trình thú vị này là cách lý tưởng để tận hưởng trải nghiệm văn hóa và ẩm thực đích thực. Nó cũng hoàn hảo cho những người đầu bếp nghiêm túc tại nhà muốn học cách nấu những món ăn Việt Nam tốt cho sức khỏe cho gia đình và bạn bè, cho những người vợ/chồng đi cùng vợ hoặc chồng trong chuyến công tác hoặc những du khách thích phiêu lưu muốn khám phá nhiều thứ hơn là chỉ ngắm cảnh tuyệt vời của chúng tôi. thành phố.', N'
~Khu sinh thái Topas
Thời gian: 3 tiếng (xấp xỉ)',N'Nà Chiềng, Mai Châu, Hòa Bình, Việt Nam', N'Nà Chiềng, Mai Châu, Hòa Bình, Việt Nam', '/tours/217');
INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'[Biking Tour] Discovering Mai Chau (Half-day)', N'Văn hóa Dao Đỏ (làng Lech Dao) - Tour du lịch Sapa nửa ngày từ Topas. Làng Lech Dao nằm trên vùng núi đồi của xã Thanh Kim, nơi sinh sống của người Dao đỏ. Chuyến đi bộ nửa ngày này mang đến cho bạn cơ hội gặp gỡ người dân địa phương, thăm nhà họ và tìm hiểu về văn hóa Dao Đỏ.
Tận hưởng khung cảnh tuyệt đẹp của thung lũng lúa và chụp một số bức ảnh tuyệt vời về phong cảnh.', N'
~Lào Chải
Thời gian: 6 tiếng (xấp xỉ)',N'Nà Chiềng, Mai Châu, Hòa Bình, Việt Nam', N'Nà Chiềng, Mai Châu, Hòa Bình, Việt Nam', '/tours/218');
INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Mai chau village and rice terrace trek', N'Lớp học Batik Hmong là buổi giới thiệu cả ngày về nghệ thuật sản xuất cây gai dầu, thiết kế batik và nhuộm chàm. Điều này mang lại cái nhìn sâu sắc hiếm có về sự phong phú của văn hóa Hmong khi ở trong môi trường gia đình ấm áp, thân thiện. Mỗi học sinh sẽ tạo ra một bức tượng sáp, thiết kế theo phong cách Hmong trên khung dệt bằng sợi gai hoặc bông. Do hạn chế về thời gian nên không thể hoàn thành phần nhuộm của quy trình (nếu bạn ở Sapa vài ngày, việc này sẽ được sắp xếp). Cây gai dầu tự nhiên tạo nên một tác phẩm hoàn thiện đẹp mắt.
Các lớp học diễn ra tại hội thảo Sapa của chúng tôi. Các khóa học bắt đầu vào khoảng 9 giờ sáng (chúng tôi linh hoạt) và thường kéo dài từ 5 đến 6 giờ. Ngày này bao gồm thông tin về cộng sinh học của người Hmong, tầm quan trọng của hàng dệt và lịch sử.', N'
~Tour Sapa Nhà Dân Chàm
Thời gian: 6 tiếng (xấp xỉ)',N'J264+XH8, Mai Hịch, Mai Châu, Hòa Bình, Việt Nam', N'J264+XH8, Mai Hịch, Mai Châu, Hòa Bình, Việt Nam', '/tours/219');
INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Northwest Loop Adventure 11 days 10 nights', N'Xưởng dệt – 54 cộng đồng dân tộc thiểu số khác nhau ở Việt Nam nổi tiếng với loại vải dệt tuyệt vời được sử dụng để tạo ra các trang phục truyền thống khác nhau. Người Dao đỏ và H''Mông đen nói riêng có một số nét quyến rũ nhất, với các chủ đề hoa văn mang tính biểu tượng, trang trí vải phức tạp, thêu tay và nhuộm tự nhiên. Hãy tham gia cùng các nghệ nhân người H''Mông da đen ở Làng Lao Chải để được giới thiệu về sản xuất cây gai dầu, thiết kế batik và nhuộm chàm tự nhiên trong suốt hội thảo dệt kéo dài cả ngày này. Bạn sẽ không trải qua tất cả 41 bước cần thiết để hoàn thành một thiết kế, nhưng bạn sẽ khám phá được ý nghĩa đằng sau một số biểu tượng nghệ thuật dân gian quan trọng nhất của người H''Mông và ngồi xuống thưởng thức bữa trưa truyền thống của người H''Mông.', N'
Lịch trình tự do',N'Hoàn Kiếm, Hà Nội, Việt Nam', N'Hoàn Kiếm, Hà Nội, Việt Nam', '/tours/220');
