INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Ngu Hanh Son & Hoi An by Night Half-Day Tour', N'Trải nghiệm cố đô của Việt Nam và những cảnh quan tuyệt đẹp của Bái Đính, Tràng An và Hang Múa trong một ngày, nơi vẻ đẹp thiên nhiên đan xen với lịch sử văn hóa phong phú.
