INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Nha Trang Cooking Class', N'Thưởng thức hương vị của Huế với trà địa phương và các món ngon miễn phí, đồng thời nuông chiều bản thân với Ngâm chân thảo dược Cung đình Huế và thư giãn bằng Bộ Gối mắt Thảo mộc Cung đình Huế. Trải nghiệm truyền thống văn hóa Việt Nam cổ xưa với kỹ thuật quấn lá chuối tại Cẩm Ôn và tự thưởng cho mình liệu trình tẩy tế bào chết toàn thân bằng 100% nguyên liệu tự nhiên như chuối chín, dừa nhiệt đới và cám gạo. 100% tự nhiên', N'
