INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Private Tour | Nha Trang Cooking Class at Memento Resort (Vietnamese, English, Korean, Japanese, Chinese Tour Guide)', N'Xe sẽ được hướng dẫn viên địa phương đón tại khách sạn. Trực tiếp lên thuyền thúng Từ làng dừa Cẩm Thanh, Vé chào mừng, nước đóng chai và trái cây tại nhà hàng của chúng tôi, bạn sẽ mặc trang phục áo dài truyền thống Việt Nam để đi thuyền thúng. trên các kênh rạch của rừng dừa nước Bảy Mẫu. Quý khách có cơ hội chiêm ngưỡng vẻ đẹp dòng sông ở vùng quê Việt Nam, thưởng thức câu cua và cách đánh bắt cá truyền thống của những ngư dân hiếu khách nơi đây. Học cách chèo thuyền thúng với người dân địa phương, Xem thuyền thúng quay, quăng lưới, bắt cua, Trở về nhà hàng của chúng tôi, Chúng tôi sẽ đưa bạn về khách sạn', N'
