INSERT INTO Tour(TourName,Overview,Schedule,DepartureLocation,DropOffLocation,PhotoPath)  
    VALUES(N'Private City Transfers from Tan Son Nhat Airport to Vung Tau/Mui Ne (Binh Thuan)/My Tho/Can Tho/Binh Duong/Da Lat', N'Chiêm ngưỡng vẻ đẹp bán đảo Sơn Trà với chùa Linh Ứng nơi đặt tượng Quan Âm cao nhất Việt Nam
Khám phá Ngũ Hành Sơn, với làng đá cẩm thạch truyền thống Non Nước và những hang động, chùa huyền bí
Tham quan Phố cổ Hội An, vẻ đẹp quyến rũ về đêm khi đèn lồng được thắp sáng
Bao gồm bữa tối kiểu Hội An, với các món đặc sản phố cổ như Cao Lầu hoặc cơm gà Hội An
