INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 3, N'Halong Bay tour-3*/4* Cruises', N'Halong Bay-2D/1N tour: Staying at lovely 3/4 stars cruise to HALONG BAY, big window, cozy cabins, private bathroom with full of toiletries OCEAN CABINS-GREAT TOUR: Crowded areas: walk in/hike up Sung Sot cave - walk/swim/ hike up TiTop island-visit pearl farm or floating village, kayaking ACTIVITIES ON BOAT INCLUDED: Squid fishing, cooking DEMO class, sunset party, Taichi(optional) Duration: 2 days: Cruising overnight in Halong Bay , crowded areas Food: halal , vegan, vegetarian, western food, Vietnamese food, buffet brunch Route of tour: Hanoi - Halong Bay - Sung Sot cave - Ti Top island - kayaking in Luon Cave - less touristy area Vehicle type: Modern, safe , fast, comfortable shuttle bus TRANSFER: Transfer 2 ways Hanoi to cruise harbor and return ~ 3.5 hours/way. 100% TRAVELERS HAPPY !', 0 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 3, N'2d/1n-BEST Bai Tu Long Bay', N'Best options-3/4 stars cruises: Swan Cruises/Renea Cruise/ Paloma Cruise/ Viola Cruise/ Cristina Diamond Cruise/Oriental Sails ACTIVITIES: Visit Thien Canh Son cave, wild and great beach, less touristy areas. Kayaking/Swimming around floating village/islets/rocks ACTIVITIES ON BOAT INCLUDED: Squid fishing, sunset party, cooking DEMO class, ROMANTIC DINNER on cruise sundeck, Taichi in morning Duration: 2 days: Cruising overnight in BAI TU LONG Bay, less touristy area Food: halal , vegan, vegetarian, western food, Vietnamese food, buffet brunch Route of tour: Hanoi - bai tu long Bay - Thien canh son cave - VOng vieng floating village Vehicle type: Modern, safe , fast, comfortable shuttle bus TRANSFER: 2 ways from Hanoi to cruise harbor and return ~ 3.5 hours/way. 100% TRAVELERS HAPPY ! Starting points: Bai Tu Long Bay, Quảng Ninh, Vietnam Hanoi, Vietnam Hạ Long Bay, Thành phố Hạ Long, Quảng Ninh, Vietnam Hạ Long Bay, Thành phố Hạ Long, Quảng Ninh, Vietnam Hạ Long, Quảng Ninh, Vietnam Lan Ha Bay, Vietnam Tuan Chau Island, Tuần Châu, Hạ Long, Quảng Ninh, Vietnam Tuan Chau Island, Tuần Châu, Hạ Long, Quảng Ninh, Vietnam', 6 );
