INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 209, N'Halong Bay tour-3*/4* Cruises', N'Halong Bay-2D/1N tour: Staying at lovely 3/4 stars cruise to HALONG BAY, big window, cozy cabins, private bathroom with full of toiletries OCEAN CABINS-GREAT TOUR: Crowded areas: walk in/hike up Sung Sot cave - walk/swim/ hike up TiTop island-visit pearl farm or floating village, kayaking ACTIVITIES ON BOAT INCLUDED: Squid fishing, cooking DEMO class, sunset party, Taichi(optional) Duration: 2 days: Cruising overnight in Halong Bay , crowded areas Food: halal , vegan, vegetarian, western food, Vietnamese food, buffet brunch Route of tour: Hanoi - Halong Bay - Sung Sot cave - Ti Top island - kayaking in Luon Cave - less touristy area Vehicle type: Modern, safe , fast, comfortable shuttle bus TRANSFER: Transfer 2 ways Hanoi to cruise harbor and return ~ 3.5 hours/way. 100% TRAVELERS HAPPY !', 10 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 209, N'2d/1n-BEST Bai Tu Long Bay', N'Best options-3/4 stars cruises: Swan Cruises/Renea Cruise/ Paloma Cruise/ Viola Cruise/ Cristina Diamond Cruise/Oriental Sails ACTIVITIES: Visit Thien Canh Son cave, wild and great beach, less touristy areas. Kayaking/Swimming around floating village/islets/rocks ACTIVITIES ON BOAT INCLUDED: Squid fishing, sunset party, cooking DEMO class, ROMANTIC DINNER on cruise sundeck, Taichi in morning Duration: 2 days: Cruising overnight in BAI TU LONG Bay, less touristy area Food: halal , vegan, vegetarian, western food, Vietnamese food, buffet brunch Route of tour: Hanoi - bai tu long Bay - Thien canh son cave - VOng vieng floating village Vehicle type: Modern, safe , fast, comfortable shuttle bus TRANSFER: 2 ways from Hanoi to cruise harbor and return ~ 3.5 hours/way. 100% TRAVELERS HAPPY ! Starting points: Bai Tu Long Bay, Quảng Ninh, Vietnam Hanoi, Vietnam Hạ Long Bay, Thành phố Hạ Long, Quảng Ninh, Vietnam Hạ Long Bay, Thành phố Hạ Long, Quảng Ninh, Vietnam Hạ Long, Quảng Ninh, Vietnam Lan Ha Bay, Vietnam Tuan Chau Island, Tuần Châu, Hạ Long, Quảng Ninh, Vietnam Tuan Chau Island, Tuần Châu, Hạ Long, Quảng Ninh, Vietnam', 5 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 209, N'2D/1N-BEST SELLER-HALONG 3*', N'Visit HALONG BAY: walk in/hike up Sung Sot cave - walk/swim/hike up TiTop island-visit pearl farm floating village, kayaking through/in cave LOVELY CABINS: Spacious & great cabins, with big window, private bathroom, great air-con and amenities. Nice food: Food(vegan,halal food, vegetarian) and services are served with good quality during your trip. Duration: 2 days: Spend 24 hours cruising to crowded area in HALONG BAY, Sung Sot cave, kayaking in Luon cave CABIN AMENITIES 1: Air-con, slippers, big window, tea-coffee free, free bottles of waters complimentary CABIN AMENITIES 2: Towels, private bathroom with standing shower slippers, full of toiletries. Pick up: We also pick up at hotels/points in Halong city TRANSFER: Transfer 2 ways Hanoi to cruise harbor and return 2 ~ 3.5 hours/way. 100% TRAVELERS HAPPY !', 20 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 209, N'2d/1n-Mid Range: Ha Long Bay', N'Window SEA VIEW CABINS: Pelican Cruise/Aclass Stellar Cruise/Oasis Bay Cruise/Royal Palace Cruise/Carina Cruise/Glory Legend Cruise/Silver Sea cruise Visit Halong Bay: with crowded attractions: Sung Sot cave, swim & hike Titop island, kayaking or bamboo boat trip around cave. EXTRA SERIVCES INCLUDED: Squid fishing, cooking DEMO class, sunset party, Taichi in morning TRANSFER: 2 ways Hanoi to cruise harbor and return ~ 3.5 hours/way. 100% TRAVELERS HAPPY !', 4 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 209, N'Balcony cabins-halong bay tour', N'Mid range cruises-Balcony: Visit crowded areas: walk in/hike up Sung Sot cave, walk/swim/ hike up Titop island-kayaking in cave and more ACTIVITIES ON BOAT INCLUDED: Squid fishing, cooking DEMO class, sunset party, Taichi(optional) Duration: 2 days: Cruising overnight in Halong Bay in crowded areas TRANSFER: Transfer 2 ways Hanoi to cruise harbor and return ~ 3.5 hours/way. 100% TRAVELERS HAPPY !', 19 );
