INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 262, N'Transfer from Hue to Hoi An by Motorbike', N'On this full-day motorbike tour and transfer hybrid, explore some of the fabulous roads from Hue to Hoi An by motorbike. There are some nice stops along the way such as Elephant Spring, Lang Co Beach, Hai Van pass and Marble Mountain.You will also have time for swimming, taking photos and enjoy a seafood lunch.', 16 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 264, N'Hue City Full Day Tour with Ancient Tombs and Conical Hat Village', N'1. The tour bring you a lot of famous sightseeing such as royal tombs, pagodas, romantic Perfume River, the busiest markets and bridges. 2. You have chance to learn about the history, architecture and the culture of Hue ancient city. 3. Explore the daily life of local people and overview of the harmonious combination of the characteristics of the urban and rural area. 4. Experienced process of maiking conical hat in Tay Ho villgage where most of conical hats produced in central Vietnam', 3 );
