INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 274, N'Scooter option with driver', N'itinerary for scooter option: You will be carried on the back of the scooter to go for different dishes in not only the center but also out of the city Scooter: Each person will be carried by one driver of scooter', 7 );
INSERT INTO Package(TourID,PackageName,[Description],MaxPeople) VALUES( 274, N'Cyclo option', N'Food tour with cyclo: Seating on Cyclo to go around the city to taste different dishes. Cyclo: Each person will be carried by one cyclo', 9 );
